import React from 'react'
import { useReducer } from 'react'
import CartContext from './CartContext'

const defaultStart = {
    item : [],
    totalAmount : 0
}
const cartReducer = (state, action) => {
  if(action.type === "ADD_TO_CART"){
      const updateItem = state.item.concat(action.item);
      const newTotalamout = state.totalAmount + action.item.price * action.item.amount;

      return {
        item : updateItem,
        totalAmount : newTotalamout
      };
  }
  if(action.type === "REMOVEITEM"){

  }

  return defaultStart;

}



function CartProvider (props) {
  const [CartState, dispatchCartAction ] = useReducer(cartReducer, defaultStart);


  const addCartHandler = (item) => {
    dispatchCartAction({type : "ADD_TO_CART", item : item})

  }

  const removeCartHandler = (id) => {
    dispatchCartAction({type : "REMOVEITEM", id : id})

  }


  const cartContext = {
    items : CartState.item,
    totalAmount : CartState.totalAmount,
    addItem : addCartHandler,
    removeItem : removeCartHandler

  }
  return (
      <CartContext.Provider value={cartContext}>
        {props.children}
      </CartContext.Provider>
  )
}

export default CartProvider;
